import React from 'react';

const ParagraphInput = ({ paragraph: { title, body }, onChange }) => {
    return (
        <div>
            <input id="paragraph-title" type="text" name="pt1" value={title} onChange={onParagraphChange} />
            <textarea cols="30" rows="5" placeholder="Type here..." name="pb1" value={body} onChange={onParagraphChange} />
            <br />
        </div>
    )
}

export default ParagraphInput
