import React from 'react';
import { connect } from 'react-redux';
import { openAddRecipeForm } from '../../../actions/dashboard';

const EditButtons = ({ openAddRecipeForm }) => {

    const onNewRecipe = () => {
        openAddRecipeForm();
    }

    return (

        <div className="row align-right">
            <button onClick={onNewRecipe} className="btn-black">+ New Recipe</button>
        </div>

    )
}

export default connect(null, { openAddRecipeForm })(EditButtons);
