import {
    LOGIN_SUCCESS
} from '../actions/types';

const initialState = {
    isAuthenticated: null,
    loading: true,
    user: null,
    token: null
}

export default function(state = initialState, action) {
    const { type, payload } = action;

    switch(type) {

        case LOGIN_SUCCESS: 
            return {
                ...state,
                isAuthenticated: true,
                loading: false,
                user: payload
            }

        default: 
            return {
                ...state
            }
    }
}
