import React, { Fragment, useState } from 'react';
import { connect } from 'react-redux';
import { login } from '../../../actions/auth';


const Login = () => {
    const [formData, setFormData] = useState({
        name: '',
        password: ''
    })

    const { name, password } = formData;


    const onChange = e => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    }

    const onSubmit = async e => {
        e.preventDefault();

        login(name, password);
    }

    return (
        <Fragment>
            <div class="dash">


                <div class="container auth" style={{width: '50%'}} >
                    <div class="card">
                        <h1><img src="hops-active.png" alt="" style={{height: '50px'}} /><span id="logintitle">Login</span></h1>
                        <br /> <br />
                        <form onSubmit={onSubmit}>
                            <div class="form-group">
                                <label htmlFor="name">Name</label>
                                <input id="name" type="text" name="name" value={name} onChange={onChange} />
                            </div>

                            <div class="form-group">
                                <label htmlFor="password">Password</label>
                                <input id="password" type="text" name="password" value={password} onChange={onChange} />
                            </div>
                            <br />
                            <button class="btn-black btn-yellow" type="submit"> Log in </button>
                        </form>

                        <p class="text-warning">
                            No Account? <a href="#!">&nbsp; Register</a>
                        </p>

                    </div>
                </div>
            </div>
        </Fragment>
    )
}

export default connect(null, { login })(Login);
