import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { getRecipes } from '../../actions/recipe';
import Spinner from '../layout/Spinner';
import CategoryRecipesItem from './CategoryRecipesItem';

const CategoryRecipes =  ({ recipe: { recipes, loading }, getRecipes }, activeCategory) => {
    useEffect(() => {
        getRecipesByCategory();
    }, [getRecipes]);


    return (

        loading ? <Spinner /> : 

            <div className="main-container" id="main-container">
                {recipes
                    .map(recipe => (  
                        <CategoryRecipesItem key={recipe._id} recipe={recipe} />
                ))}
            </div>
    )
}

const mapStateToProps = state => ({
    recipe: state.recipe,
    activeCategory: state.category.activeCategory

})

export default connect(mapStateToProps, { getRecipes })(CategoryRecipes);
