import axios from 'axios';
import { url } from '../config';
import { 
    CHANGE_EDIT_MODE,
    CLOSE_ADD_RECIPE_FORM,
    SELECT_EDIT_RECIPE
} from './types';


// Open add recipe form 
export const changeEditMode = mode => async dispatch => {
    dispatch({
        type: CHANGE_EDIT_MODE,
        payload: mode
    })
}



// Save new recipe 
export const saveNewRecipe = formData => async dispatch => {
    try {

        console.log(packagedFormData);

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.post(`${url}/api/recipes`, formData, config);

        dispatch({
            type: CLOSE_ADD_RECIPE_FORM,
            payload: res.data.data
        })

    } catch (err) {
        console.log(err.message);
    }
} 


// Update recipe
export const updateRecipe = (id, formData) => async dispatch => {
    try {

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.put(`${url}/api/recipes/${id}`, formData, config);

        dispatch({
            type: CLOSE_ADD_RECIPE_FORM,
            payload: res.data.data
        })
    } catch (err) {
        console.log(err.message)
    }
}


// Select a recipe by id
export const selectEditRecipe = id => async dispatch => {
    try {
        const res = await axios.get(`${url}/api/recipes?_id=${id}`);

        dispatch({
            type: SELECT_EDIT_RECIPE,
            payload: res.data.data[0] 
        })

    } catch (err) {
         console.error(err.message);
    } 
}


// Delete recipe
export const deleteRecipe = id => async dispatch => {
    try {
        const res = axios.delete(`${url}/api/recipes/${id}`);
    } catch (err) {
        console.log(err.message);
    }
}
