import React from 'react'; 
import { connect } from 'react-redux';
import { changeEditMode, selectEditRecipe } from '../../../actions/dashboard';

const RecipeListItem = ({ recipeTitle, recipeId, selectEditRecipe, changeEditMode }) => {
    const onClick = () => {
        selectEditRecipe(recipeId);
    }

    return (
        <li>
            <a href="#!">{recipeTitle}</a>
            <button onClick={onClick} className="btn-grey">Edit</button>
        </li>
    )
}


export default connect(null, { changeEditMode, selectEditRecipe })(RecipeListItem);
