import React, { Fragment } from 'react';
import { url } from '../../config';

const CategoryRecipesItem = ({ recipe: { title, subtitle, category, paragraphs, image } }) => {

    let recipeImage = `${url}/client/public/${recipeImage}`;

    return (
        <Fragment>
            <h1>{title}</h1>
            <p className="muted">{subtitle}</p>
            <div className="blog-img" style={{backgroundImage: `url(${recipeImage})`}}></div>

        </Fragment>
    )
}

export default CategoryRecipesItem;
