import axios from 'axios';
import { url } from '../config';
import { 
    CHANGE_EDIT_MODE,
    CLOSE_ADD_RECIPE_FORM,
    SELECT_EDIT_RECIPE,
    ClOSE_ADD_CATEGORY_FORM
} from './types';


// Open add recipe form 
export const changeEditMode = mode => async dispatch => {
    dispatch({
        type: CHANGE_EDIT_MODE,
        payload: mode
    })
}



// Save new recipe 
export const saveNewRecipe = (formData, imgData) => async dispatch => {
    try {

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.post(`${url}/api/recipes`, formData, config);

        // upload image
        let fd;

        if (selectedImage) {
            fd = new FormData();
            fd.append('imageFile', selectedImage, selectedImage.name)
        }

        const configImg = {
            headers: {
                'Content-Type': 'multi-part/formdata'
            }
        }

        await axios.put(`${url}/api/recipes/${res.data.data._id}/photo`, imgData, configImg)

        dispatch({
            type: CLOSE_ADD_RECIPE_FORM,
            payload: res.data.data
        })

    } catch (err) {
        console.log(err.message);
    }
} 


// Update recipe
export const updateRecipe = (id, formData) => async dispatch => {
    try {

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.put(`${url}/api/recipes/${id}`, formData, config);

        dispatch({
            type: CLOSE_ADD_RECIPE_FORM,
            payload: res.data.data
        })
    } catch (err) {
        console.log(err.message)
    }
}


// Select a recipe by id
export const selectEditRecipe = id => async dispatch => {
    try {
        const res = await axios.get(`${url}/api/recipes?_id=${id}`);

        dispatch({
            type: SELECT_EDIT_RECIPE,
            payload: res.data.data[0] 
        })

    } catch (err) {
         console.error(err.message);
    } 
}


// Delete recipe
export const deleteRecipe = id => async dispatch => {
    try {
        const res = axios.delete(`${url}/api/recipes/${id}`);
    } catch (err) {
        console.log(err.message);
    }
}


// Save new category
export const saveCategory = (formData) => async dispatch => {
    try {
        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }

        const res = await axios.post(`${url}/api/categories`, formData, config);


        dispatch({
            type: ClOSE_ADD_CATEGORY_FORM
        })

    } catch (err) {

    }
}


