// Redux
import { Provider } from 'react-redux';
import { store } from './store';

import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './app.css';
import Landing from './components/layout/Landing';
import CategoryBand from './components/categories/CategoryBand';
import CategoryRecipes from './components/main/CategoryRecipes';
import Recipe from './components/main/Recipe';
import { clearSelectedRecipe } from './actions/recipe';
import { connect } from 'react-redux';





const App = () => {

  const clear = () => {
    console.log('Left page');
  };

  return (
    <Provider store={store}>
      <Router>
        <div>
          <Landing />
          <CategoryBand />
            <Switch>
              <Route exact path='/:category' component={CategoryRecipes} />
              <Route exact path='/:category/:slug' component={Recipe} onLeave={clear} />
            </Switch>
        </div>
      </Router>
    </Provider>
  );
}

export default connect(null, { clearSelectedRecipe })(App);
