import React from 'react';
import ReactDOM from 'react-dom';

const AddRecipeForm = () => {

    const onCreateParagraph = (e) => {
        e.preventDefault(); 

        const label = React.createElement("label", {for: 'paragraph-title'}, 'Paragraph Title');
        const input = React.createElement("input", {id: "paragraph-title", type: "text", placeholder: "Enter paragraph title"});
        const textArea = React.createElement("textarea", {name: "paragraph-body", cols: "30", rows: "5", placeholder: "Enter paragraph"});
        const nextParagraph = React.createElement("div", {id: 'next-paraghraph'});
        const formGroup = React.createElement("div", {className: 'form-group'}, [label, input]);

        const container = React.createElement("div", null, [formGroup, textArea, nextParagraph])

        // ReactDOM.render(container, document.getElementById('form').lastElementChild);

        ReactDOM.render(
            <div className="red">
              <div className="blue" />
            </div>,
            document.querySelector('#nex-paragraph')
          )
    }


    return (
        <div className="dash-card">
            <div className="dash-card-header">
                Add new Recipe
            </div>
            <div className="dash-card-body">
                <form>

                <div id="form">
                    <div className="form-group">
                        <label for="name">Recipe Name</label>
                        <input id="name" type="text" placeholder="Enter recipe name" />
                    </div>

                    <div className="form-group">
                        <label for="subtitle">Subtitle</label>
                        <input id="subtitle" type="text" placeholder="Enter subtitle" />
                    </div>

                    <div className="form-group">
                        <label for="category">Category</label>
                        <select name="category" id="category" className="select">
                            <option value="" disabled selected>Select a category</option>
                            <option value="Helles">Helles</option>
                            <option value="Heffeweizen">Heffeweizen</option>
                        </select>
                    </div>
                    <br /><br />
                    <div className="form-group">
                        <label for="paragraph-title">Paragraph Title</label>
                        <input id="paragraph-title" type="text" placeholder="Enter paragraph title" />
                    </div>

                    <textarea name="paragraph-body" cols="30" rows="5" placeholder="Enter paragraph"></textarea>

                    <div id="next-paragraph"></div>
                </div>    

                    <button onClick={onCreateParagraph} className="center btn-black ">+ Add Paragraph</button>
                </form>
            </div>
        </div>
    )
}




export default AddRecipeForm;
