import {
    CHANGE_EDIT_MODE,
    CLOSE_ADD_RECIPE_FORM
} from '../actions/types';

const initialState = {
    editMode: null,
    editRecipe: null
}

export default function(state = initialState, action) {
    const { type, payload } = action;

    switch (type) {

        case CHANGE_EDIT_MODE:
            return {
                ...state,
                editMode: payload.mode,
                editRecipe: payload.recipe
            }

        case SELECT_RECIPE:
            return {
                ...state, 
                editMode: 'editRecipe',
                editRecipe: payload.recipe
            }

        case CLOSE_ADD_RECIPE_FORM:
            return {
                ...state,
                editMode: null
            }

        default:
            return {
                ...state
            } 
    }
}
