import React from 'react';
import { url } from '../../config';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

const CategoryItem = ({ 
    category: { activeCategory },
    categories: { _id, title, iconActive, iconInactive, iconHover },
    }) => {
    return (
        <div className="col">

            <div className={`inner-category ${activeCategory._id === _id && 'active'}`}>
                <div className="icon-wrap">
                    <img id="icon-image" src={`${url + activeCategory._id === _id ? iconActive : iconInactive}`} />
                    <Link id="icon-link" className={activeCategory._id === _id && 'active-link'} href="#!">{title}</Link>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    category: state.category
});

export default connect(mapStateToProps, {})(CategoryItem);
