import axios from 'axios';
import { url } from '../config';
import { 
    GET_RECIPES
} from './types';


// Get all recipes
export const getRecipes = () => async dispatch => {
    try {
        const res = await axios.get(`${url}/api/recipes`);

        // dispatch({
