import {
    CHANGE_EDIT_MODE,
    CLOSE_ADD_RECIPE_FORM,
    SELECT_EDIT_RECIPE,
    ClOSE_ADD_CATEGORY_FORM, 
    ClEAR_EDIT_STATE,
    GET_CATEGORIES,
    GET_ALL_RECIPES
} from '../actions/dashboard';

const initialState = {
    editMode: null,
    editRecipe: null,
    categories: [],
}

export default function(state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case GET_CATEGORIES: 
            return {
                ...state,
                categories: payload,
                // activeCategory: payload.filter((category) => category.title === 'Latest Recipe')[0],
                loading: false
            }

        case ClEAR_EDIT_STATE:
            return {
                ...state,
                editRecipe: null
            }

        case CHANGE_EDIT_MODE:
            return {
                ...state,
                editMode: payload
            }

        case SELECT_EDIT_RECIPE:
            return {
                ...state, 
                editMode: 'editRecipe',
                editRecipe: payload
            }

        case CLOSE_ADD_RECIPE_FORM:
        case ClOSE_ADD_CATEGORY_FORM:
            return {
                ...state,
                editMode: null,
            }

        default:
            return {
                ...state
            } 
    }
}
