import React from 'react';

const ParagraphInput = ({ paragraph: { title, body }, onChange, index }) => {

    return (
        <div>
            <input id="paragraph-title" type="text" name={index} data-part='title' value={title} onChange={onChange} />
            <textarea cols="30" rows="5" placeholder="Type here..." name={index} data-part='body'  value={body} onChange={onChange} />
            <br />
        </div>
    )
}

export default ParagraphInput
