import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { getCategories } from '../../../actions/category';
import CategorySidebarItem from './CategorySidebarItem'
import { getAllRecipes } from '../../../actions/recipe';

const CategorySidebar = ({  categories, recipes, getCategories, getAllRecipes }) => {
    useEffect(() => {
        getCategories();
        getAllRecipes();
    }, [getCategories, getAllRecipes]);  

    return (
        <div className="dash-card">
            <div className="dash-card-header">
                <img src="../../../../img/beer-categories.png" alt="" className="dash-card-icon" />
            Beer Categories
        </div>
            <div className="dash-card-body">
                <ul>

                    {categories
                        .filter(category => category.title !== 'Latest Recipe')
                        .map(category => (
                        <CategorySidebarItem key={category._id} category={category.title} 
                            recipeCount={ recipes.filter(recipe => recipe.category === category.title).length} 
                        />
                    ))}

                </ul>
            </div>

            <button id="new-category" className="btn-grey">+ New Category</button>
        </div>
    )
}


const mapStateToProps = state => ({
    categories: state.category.categories,
    recipes: state.recipe.recipes
});

export default connect(mapStateToProps, { getCategories, getAllRecipes })(CategorySidebar);
