import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { saveNewRecipe, updateRecipe, deleteRecipe } from '../../../actions/dashboard';
import { url } from '../../../config';
import Alert from '../layout/Alert';
import { setAlert } from '../../../actions/alert';
import ParagraphInput from './ParagraphInput';


const AddRecipeForm = ({ saveNewRecipe, updateRecipe, setAlert, deleteRecipe, loggedInUserId, categories, dashboard: { editMode, editRecipe } }) => {
    const [formData, setFormdata] = useState({
        title: '',
        subtitle: '',
        category: '',
        pt1: 'MALT/GRAIN BILL',
        pb1: '',
        pt2: 'HOPS/SCHEDULE',
        pb2: '',
        pt3: 'YEAST',
        pb3: '',
        pt4: 'DIRECTIONS',
        pb4: ''
    });

    const [paragraphData, setParagraphData] = useState([
        { title: 'MALT/GRAIN BILL', body: '' },
        { title: 'HOPS/SCHEDULE', body: '' },
        { title: 'YEAST', body: '' },
        { title: 'DIRECTIONS', body: '' }
    ])

    const [selectedImage, setSelectedImage] = useState(null);

    const { title, subtitle, category, pt1, pb1, pt2, pb2, pt3, pb3, pt4, pb4 } = formData;



    useEffect(() => {
        if (editMode === 'editRecipe') {
            const { _id, category, title, subtitle, user, paragraphs, image } = editRecipe;

            setFormdata({
                title,
                subtitle,
                category,
                image,
                pt1: paragraphs[0].title,
                pb1: paragraphs[0].body,
                pt2: 'HOPS/SCHEDULE',
                pb2: '',
                pt3: 'YEAST',
                pb3: '',
                pt4: 'DIRECTIONS',
                pb4: ''
            })
        }
    }, [])

    const onChange = (e) => {
        setFormdata({
            ...formData,
            [e.target.name]: e.target.value
        })
    };

    const onParagraphChange = (e) =>
        console.log('kjdf');
        setParagraphData([ 
            ...paragraphData
            ]
        )


    const selectImageFile = (e) => {
        e.preventDefault();

        setSelectedImage(e.target.files[0]);
    };


    const onSubmit = (e) => {
        e.preventDefault();

        if(title === '') {
            return setAlert('Please fill in title', 'danger')
        }

        if(selectedImage) {
            const fd = new FormData();
            fd.append('imageFile', selectedImage, selectedImage.name)
        }

        const packagedFormData = {
            user: loggedInUserId,
            title,
            subtitle,
            category,
            paragraphs: [
                { title: pt1, body: pb1 },
                { title: pt2, body: pb2 },
                { title: pt3, body: pb3 },
                { title: pt4, body: pb4 },
            ]
        };


        editMode === 'addRecipe' ? saveNewRecipe(packagedFormData) : updateRecipe(editRecipe._id, packagedFormData);

        // eslint-disable-next-line no-restricted-globals
        location.reload();
    }


    const onDelete = () => {
        // eslint-disable-next-line no-restricted-globals
        const yes = confirm("Sure to delete?");

        if (yes) {
            deleteRecipe(editRecipe._id);
        }
    }


    return (
        <div className="dash-card">
            <div className="dash-card-header">
                Add new Recipe
            </div>
            <div className="dash-card-body">
                <Alert />
                <form onSubmit={onSubmit}>
                { editRecipe && ( <img className="preview-image" src={`${url}/uploads/recipe-img/${editRecipe.image}`} />) }

                <input className="custom-file" type="file" accept=".jpg, .jpeg, .png" onChange={selectImageFile} />

                    <div className="form-group">
                        <label htmlFor="name">Recipe Name</label>
                        <input id="name" type="text" placeholder="Enter recipe name" name="title" value={title} onChange={onChange} />
                    </div>

                    <div className="form-group">
                        <label htmlFor="subtitle">Subtitle</label>
                        <input id="subtitle" type="text" placeholder="Enter subtitle" name="subtitle" value={subtitle} onChange={onChange} />
                    </div>

                    <div className="form-group">
                        <label htmlFor="category">Category</label>
                        <select id="category" className="select" name="category" value={category} onChange={onChange}>
                            <option value="" disabled>Select a category</option>

                            {
                                categories.filter(category => category.title !== 'Latest Recipe')
                                    .map((category, index) => (
                                        <option key={index} value={category.title} >{category.title}</option>
                                    ))
                            }


                        </select>
                    </div>
                    <br /><br />

                    {
                        paragraphData.map((paragraph, index) => (
                            <ParagraphInput key={index} paragraph={paragraph} onParagraphChange={onParagraphChange} />
                        ))
                    }


                    {/* <input id="paragraph-title" type="text" name="pt1" value={pt1} onChange={onChange} />
                    <textarea cols="30" rows="5" placeholder="Type here..." name="pb1" value={pb1} onChange={onChange} />
                    <br />

                    <input id="paragraph-title" type="text" name="pt2" value={pt2} onChange={onChange} />
                    <textarea cols="30" rows="5" placeholder="Type here..." name="pb2" value={pb2} onChange={onChange} />
                    <br />

                    <input id="paragraph-title" type="text" name="pt3" value={pt3} onChange={onChange} />
                    <textarea cols="30" rows="5" placeholder="Type here..." name="pb3" value={pb3} onChange={onChange} />
                    <br />

                    <input id="paragraph-title" type="text" name="pt4" value={pt4} onChange={onChange} />
                    <textarea cols="30" rows="5" placeholder="Type here..." name="pb4" value={pb4} onChange={onChange} />
                    */}
                    <div className="edit-buttons"> 

                        <button className="center edit-btn" type="submit">Save recipe</button>
                        {editMode === 'editRecipe' && (<button className="center edit-btn" onClick={onDelete} style={{ marginLeft: '10px', backgroundColor: 'red' }}>Delete Recipe</button>)}
                    </div>
                </form>
            </div>
        </div>
    )
}


const mapStateToProps = state => ({
    dashboard: state.dashboard,
    loggedInUserId: state.auth.user._id,
    categories: state.category.categories
})

export default connect(mapStateToProps, { saveNewRecipe, updateRecipe, deleteRecipe, setAlert })(AddRecipeForm);
