import React from 'react';
import { useState } from 'react';
import { connect } from 'react-redux';
import Alert from '../layout/Alert';
import CategoryIcon from './CategoryIcon';

const AddCategoryForm = () => {
    const [categoryTitle, setCategoryTitle] = useState('');

    const [categoryIcon, setCategoryIcon] = useState({
        iconActive: '',
        iconInactive: '',
        iconHover: ''
    });

    const availableCategoryIcons = ['growler', 'wineglass', 'stein', 'sour', 'coaster'];


    const onChange = (e) => {
        setCategoryTitle(e.target.value)
    };

    const onSelectCategory = (e) => {
        setCategoryIcon({
            ...categoryIcon,
            iconActive: `${url}/uploads/category-icons/${categoryIcon}-hover.png`
        })
    }


    const onSubmit = () => {

    };

    return (
        <div className="dash-card">
            <div className="dash-card-header">
                Add new Category
            </div>
            <div className="dash-card-body">
                <Alert />
                <form onSubmit={onSubmit}>

                    <div className="form-group">
                        <label htmlFor="name">Category Name</label>
                        <input id="name" type="text" placeholder="Enter category name" name="title" value={categoryTitle} onChange={onChange} />
                    </div>

                    <br/>
                    <label>Select an Icon</label><br /><br/>
                    {
                        availableCategoryIcons.map((category, index) => (
                            <CategoryIcon key={index} categoryIcon={category} onSelectCategory={onSelectCategory} />
                        ))
                    }

                    <br /><br />

                    <div className="edit-buttons">

                        <button className="center edit-btn" type="submit">Save category</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default connect()(AddCategoryForm);
