import axios from 'axios';
import { url } from '../config';
import { 
    GET_RECIPES,
    SELECT_RECIPE
} from './types';


// Get all recipes
export const getRecipesByCategory = category => async dispatch => {
    try {
        const res = await axios.get(`${url}/api/recipes?category=${category}`);

        dispatch({
            type: GET_RECIPES,
            payload: res.data.data 
        })
    } catch (err) {
         console.error(err.message);
    } 
}


// Select a recipe
export const selectRecipe = id => async dispatch => {
    try {
        const res = await axios.get(`${url}/api/recipes?_id=${id}`);

        dispatch({
            type: SELECT_RECIPES,
            payload: res.data.data 
        })
    } catch (err) {
         console.error(err.message);
    } 
}
