import {
    OPEN_ADD_RECIPE_FORM,
    CLOSE_ADD_RECIPE_FORM
} from '../actions/types';

const initialState = {
    editMode: null
}

export default function(state = initialState, action) {
    const { type, payload } = action;

    switch (type) {

        case OPEN_ADD_RECIPE_FORM:
            return {
                ...state,
                editMode: payload
            }

        case CLOSE_ADD_RECIPE_FORM:
            return {
                ...state,
                editMode: null
            }

        default:
            return {
                ...state
            } 
    }
}
