import React, { Fragment } from 'react';
import { url } from '../../config';
import { selectRecipe } from '../../actions/recipe';
import { connect } from 'react-redux';

const CategoryRecipesItem = ({ recipe: { title, subtitle, image, paragraphs } }) => {

    let recipeImage = `${url}/uploads/recipe-img/${image}`;

    const onClick = ({ title, subtitle, image, paragraphs }) => {
        selectRecipe({ title, subtitle, image, paragraphs });
        console.log({title, subtitle, image, paragraphs });
    }

    return (
        <Fragment>
            <div className="recipe-card">
                <div className="card-img" style={{backgroundImage: `url(${recipeImage})`}}></div>
                <div className="card-body">
                    <h1>{title}</h1>
                    <p className="muted">{subtitle}</p>
                    <button onClick={onClick}>See Recipe</button>
                </div>
            </div>         
        </Fragment>
    )
}

export default connect(null, { selectRecipe })(CategoryRecipesItem);
