import {
    LOGIN_SUCCESS,
    GET_USER
} from '../actions/types';

const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: null,
    loading: true,
    user: null,
    token: null
}

export default function(state = initialState, action) {
    const { type, payload } = action;

    switch(type) {

        case GET_USER:  
            return {
                ...state,
                ...payload,
                isAuthenticated: true,
                loading: false,
                user: payload
            }

        case LOGIN_SUCCESS: 
            localStorage.setItem('token', payload.token);
            return {
                ...state,
                isAuthenticated: true,
                loading: false,
                user: payload
            }

        default: 
            return {
                ...state
            }
    }
}
