import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { getCategories } from '../../../actions/category';

const CategorySidebar = ({ category: { categories }, getCategories }) => {
    useEffect(() => {
        getCategories();
    }, [getCategories]);  

    useEffect(() => {
        getCategories();
    }, [getCategories]);  

    return (
        <div className="dash-card">
            <div className="dash-card-header">
                <img src="../../../../img/beer-categories.png" alt="" className="dash-card-icon" />
            Beer Categories
        </div>
            <div className="dash-card-body">
                <ul>
                    <li><a href="#!">Helles</a><span className="badge">3</span></li>
                    <li><a href="#!">Heffeweizen</a><span className="badge">7</span></li>
                </ul>
            </div>

            <button id="new-category" className="btn-grey">+ New Category</button>
        </div>
    )
}


const mapStateToProps = state => ({
    category: state.category
});

export default connect(mapStateToProps, { getCategories })(CategorySidebar);
