import React, { Fragment } from 'react';
import { connect } from 'react-redux';
import { url } from '../../config';

const Recipe = ({ recipe: { title, subtitle, image, paragraphs } }) => {

    let recipeImage = `${url}/uploads/recipe-img/${image}`;

    return (
        <Fragment>
            <h1>{title}</h1>
            <p className="muted">{subtitle}</p>
            <div className="blog-img" style={{backgroundImage: `url(${recipeImage})`}}></div>
        </Fragment>
    )
}


const mapStateToProps = state => ({
    displayRecipe: state.recipe.selectedRecipe
});

export default connect(mapStateToProps, {})(Recipe);
