import React from 'react';
import { url } from '../../config';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { hoverCategory } from '../../actions/category';

const CategoryItem = ({ 
    category: { activeCategory, hover },
    categories: { _id, title, iconActive, iconInactive, iconHover },
    }) => {

    const hoverEffect = (e) => {
       hoverCategory(e.target.dataset.item)
    }

    return (
        <div className="col">

            <div className={`inner-category ${activeCategory._id === _id ? 'active' : ''}`}>
                <div data-item={_id} className="icon-wrap" onMouseEnter={hoverEffect}>
                    <img data-item={_id} id="icon-image" src={`${url}${
                        activeCategory._id === _id ? iconActive 
                        : hover === _id ? iconHover 
                        : iconInactive
                    }`} />
                    <Link data-item={_id} id="icon-link" className={activeCategory._id === _id ? 'active-link' : ''} to="#!">{title}</Link>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    category: state.category
});

export default connect(mapStateToProps, { hoverCategory })(CategoryItem);
