import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { getRecipes } from '../../actions/recipe';

const CategoryRecipes = ( {recipe: {recipes, loading}, getRecipes}) => {
    useEffect(() => {
        getRecipes();
    }, [getRecipes]);


    return (

        !loading && (
                    <div>
            { recipes.map(recipe => (
                <h4>{recipe.title}</h4>
            )) }
        </div>
        )


    )
}

const mapStateToProps = state => ({
    recipe: state.recipe
})

export default connect(mapStateToProps, { getRecipes })(CategoryRecipes);
