import React, { Fragment } from 'react';
import { url } from '../../config';
import CategoryItem from './CategoryItem';
import { connect } from 'react-redux';
import { getCategories } from '../../actions/category';


const CategoryBand = () => {
    return (
        <Fragment>
            <div className="categories" id="categories">
                <div className="container">

                    <CategoryItem />

                    <div className="col">
                        <div className="inner-category">
                            <div className="icon-wrap" data-category="heffeweizen">
                                <img id="icon-image" src={`${url}/uploads/category-icons/heffeweizen-inactive.png`} />
                                <a id="icon-link" href="">Heffeweizen</a>
                            </div>
                        </div>
                    </div>

                    <div className="col">
                        <div className="inner-category">
                            <div className="icon-wrap" data-category="helles">
                                <img id="icon-image" src={`${url}/uploads/category-icons/helles-inactive.png`} />
                                <a id="icon-link" href="">Helles</a>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        </Fragment>
    )
}


const mapStateToProps = state => ({
    category: state.category
})

export default connect(mapStateToProps, { getCategories })(CategoryBand);
