import React, { Fragment } from 'react';
import { url } from '../../config';

const CategoryRecipesItem = ({ recipe: { title, subtitle, image } }) => {

    let recipeImage = `${url}/uploads/recipe-img/${image}`;

    return (
        <Fragment>
            <div className="recipe-card">
                <div className="card-img" style={{backgroundImage: `url(${recipeImage})`}}></div>
                <div className="card-body">
                    <h1>{title}</h1>
                    <p className="muted">${subtitle}</p>
                    <button data-recipe-id="${recipe._id}" id="see-recipe-btn">See Recipe</button>
                </div>
            </div>         
        </Fragment>
    )
}

export default CategoryRecipesItem;
